    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
String user,pass;
user=jTextField1.getText();
pass=new String(jPasswordField1.getPassword());
if(user.equals("administrator") && pass.equals("007")){
admincontrol a=new admincontrol();
a.setVisible(true);
this.dispose();
}
else{
try{
Class.forName("java.sql.Driver");
Connection con=DriverManager.getConnection("jdbc:mysql://localhost/pukar","root","root");
Statement stmt= con.createStatement();
String query="select * from users where USERNAME='"+user+"' and password='"+pass+"';";
ResultSet rs= stmt.executeQuery(query);
       if (rs.next()){
           calamities a=new calamities();
           a.jTextField1.setText(user);
           a.setVisible(true);
           this.dispose();
       }
       else{
       JOptionPane.showMessageDialog(null,"Incorrect Password or Username.");
       }
rs.close();
stmt.close();
con.close();
}catch(Exception e){
System.out.println(e);
}
}
    }//GEN-LAST:event_jButton2ActionPerformed

